import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { Container } from '@mui/material';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Button, CardActionArea, CardActions } from '@mui/material';
import Grid from '@mui/material/Grid';
import { useDispatch, useSelector } from 'react-redux';
import appsettings from '../../appsettings.json'
import axios from 'axios';
import { Link } from "react-router-dom"
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

const Home = () => {

    const dispatch = useDispatch()
    const [pais, setPais] = useState("ar")
    const [size, setSize] = useState(10)
    const [cargando, setCarga] = useState(true)
    const noticias = useSelector((state) => state.noticiasReducer)

    const changePais = (event) =>{
        setPais(event.target.value)
    }

    const changeNews = async (page = 1, pageSize = size, paisSelect = pais) => {

        if (paisSelect !== pais) {
            setPais(paisSelect)
        }
        if (pageSize !== size) {
            setSize(pageSize)
        }

        const response = await axios.get(
            appsettings.BaseApi + 'top-headlines?country=' + pais + '&page=' + page + '&pageSize=' + size
        )
    }

    return (
        <Container fixed sx={{ marginTop: '30px' }}>
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }} justifyContent='center'>
                <Grid md={10}>
                    <Typography variant="h4" gutterBottom>
                        h3. Heading
                    </Typography>
                    <Grid container justifyContent='space-between'>
                        <Grid md={4}>
                            <Typography variant="overline" display="block" gutterBottom>
                                Ultimas Noticias /
                                <Link href='/buscador' to='/buscador'> Buscador</Link>
                            </Typography>
                        </Grid>
                        <Grid md={2}>
                            <FormControl variant="standard" sx={{ m: 1, minWidth: 180 }}>
                                <InputLabel id="demo-simple-select-standard-label">Pais</InputLabel>
                                <Select
                                    labelId="demo-simple-select-standard-label"
                                    id="demo-simple-select-standard"
                                    value={pais}
                                    onChange={changePais}
                                    label="Pais"
                                >
                                    <MenuItem value={10}>Ten</MenuItem>
                                    <MenuItem value={20}>Twenty</MenuItem>
                                    <MenuItem value={30}>Thirty</MenuItem>
                                </Select>
                            </FormControl>
                            <FormControl variant="filled" sx={{ m: 1, minWidth: 120 }}>
                            </FormControl>
                        </Grid>
                    </Grid>
                </Grid>
                <Grid md={10}>
                    <Card>
                        <CardActionArea>
                            <CardMedia
                                component="img"
                                height="140"
                                image="/static/images/cards/contemplative-reptile.jpg"
                                alt="green iguana"
                            />
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="div">
                                    Lizard
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                    Lizards are a widespread group of squamate reptiles, with over 6,000
                                    species, ranging across all continents except Antarctica
                                </Typography>
                            </CardContent>
                        </CardActionArea>
                        <CardActions>
                            <Button size="small" color="primary">
                                Share
                            </Button>
                        </CardActions>
                    </Card>
                </Grid>
            </Grid>
        </Container>
    );
};

Home.propTypes = {

};

export default Home;
