import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { Container } from '@mui/material';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Button, CardActionArea, CardActions } from '@mui/material';
import Grid from '@mui/material/Grid';
import { useDispatch, useSelector } from 'react-redux';
import appsettings from '../../appsettings.json'
import axios from 'axios';
import { Link } from "react-router-dom"
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Pagination from '@mui/material/Pagination';
import { setAllNews } from '../../redux/reducers/noticiasReducer';
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';

const News = () => {

    const noticias = useSelector((state) => state.noticiasReducer)

    return (
        <Grid md={12}>
            {noticias.all.map((noticia) => (
                    <Card>
                        <CardMedia
                            component="img"
                            height="140"
                            image={noticia.urlToImage}
                            alt="green iguana"
                        />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="div">
                                {noticia.title}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                                {noticia.description}
                            </Typography>
                        </CardContent>
                        <CardActions>
                            <a to={noticia.url} href={noticia.url} about='_blank' style={{ color: 'orange' }}> Ver mas...</a>
                        </CardActions>
                    </Card>
            ))}
            </Grid>
    );
};

News.propTypes = {

};

export default News;
